mapper.insert:
INSERT INTO UNI_STU (
	STU_NO
	, COLL_NO
	, DEPT_NO
	, DOUBLE_NO
	, STU_YEAR
	, STU_SEM
	, STU_STATUS
	, MEM_NO
	, PARENT_NAME
	, PARENT_TELL
	, STU_BANK_NAME
	, STU_BANK_ACCOUNT			
) VALUES (
	#{stuNo}
	, #{collNo}
	, #{deptNo}
	, #{doubleNo}
	, #{stuYear}
	, #{stuSem}
	, #{stuStatus}
	, #{memNo}
	, #{parentName}
	, #{parentTell}
	, #{stuBankName}
	, #{stuBankAccount}
)

INSERT INTO UNI_EMP (
	EMP_NO
	, EMP_TYPE
	, DEPT_NO
	, MEM_NO
	, COLL_NO
) VALUES (
	#{empNo}
	, #{empType}
	, #{deptNo}
	, #{memNo}
	, #{collNo}
)

mapper.select:
SELECT APPLY_NO , MEM.MEM_NAME AS MEM_NAME , MEM.MEM_NO AS MEM_NO , APPLY_CODE , TO_CHAR(APPLY_DATE, 'YYYY-MM-DD') AS APPLY_DATE , APPLY_REASON , TO_CHAR(APPLROVAL_DATE, 'YYYY-MM-DD') AS APPLROVAL_DATE , PROCESS_STATUS , FROM_COLL , (SELECT COLL_NAME FROM COLLEAGE WHERE DEPT.FROM_COLL= COLLEAGE.COLL_NO) AS FROM_COLL_NAME , TO_COLL , (SELECT COLL_NAME FROM COLLEAGE WHERE DEPT.TO_COLL= COLLEAGE.COLL_NO) AS TO_COLL_NAME , FROM_DEPT , (SELECT DEPT_NAME FROM DEPT WHERE DEPT.FROM_DEPT= DEPT.DEPT_NO) AS FROM_DEPT_NAME , TO_DEPT , (SELECT DEPT_NAME FROM DEPT WHERE DEPT.TO_DEPT= DEPT.DEPT_NO) AS TO_DEPT_NAME , DOUBLE_MAJOR_COLL , (SELECT COLL_NAME FROM COLLEAGE WHERE DEPT.DOUBLE_MAJOR_COLL = COLLEAGE.COLL_NO) AS DOUBLE_MAJOR_COLL_NAME , DOUBLE_MAJOR_DEPT , (SELECT DOUBLE_DEPT_NAME FROM DOUBLE_MAJOR WHERE DEPT.DOUBLE_MAJOR_DEPT = DOUBLE_MAJOR.DOUBLE_NO) AS DOUBLE_MAJOR_DEPT_NAME , DEPT.STU_YEAR , DEPT.STU_SEM FROM DEPT_MANAGE DEPT, UNI_MEMBER MEM, UNI_STU STU WHERE MEM.MEM_NO = STU.MEM_NO AND DEPT.STU_NO = STU.STU_NO
AND PROCESS_STATUS = #{processStatus}
AND TO_CHAR(APPLY_DATE, 'YYYY-MM-DD') <= #{fromDate}
AND TO_CHAR(APPLY_DATE, 'YYYY-MM-DD') >= #{toDate}

mapper.update:
UPDATE UNI_STU
SET COLL_NO = #{collNo}
	, DEPT_NO = #{deptNo}
WHERE STU_NO = #{stuNo}

mapper.select:
SELECT TO_COLL , TO_DEPT , STU_NO FROM DEPT_MANAGE WHERE APPLY_NO IN
#{applyCode}
AND PROCESS_STATUS NOT LIKE '승인완료'
