mapper.select:
SELECT BOARD_NO , BOARD_TITLE , BOARD_CONTENT , BOARD_WRITER , TO_CHAR(REG_BOARD_DATE,'YYYY-MM-DD') AS REG_BOARD_DATE , TO_CHAR(REG_BOARD_DATE,'YYYY-MM-DD HH24:MI:SS') AS REG_BOARD_DATE_ORDER , READ_CNT , CATE_NO , REPLY_CNT , IS_PRIVATE , IS_NOTICE FROM UNI_BOARD WHERE BOARD_WRITER = #{boardWriter}
AND TO_CHAR(REG_BOARD_DATE, 'YYYYMMDD' ) >= TO_CHAR(ADD_MONTHS(SYSDATE, #{month}), 'YYYYMMDD')
AND TO_CHAR(REG_BOARD_DATE, 'YYYY-MM-DD') >= #{fromDate} AND TO_CHAR(REG_BOARD_DATE, 'YYYY-MM-DD') <= #{toDate}
AND UPPER(${searchKeyword}) LIKE '%' || UPPER(#{searchValue}) || '%'
ORDER BY ${orderBy} DESC OFFSET #{offsetCnt} ROWS FETCH FIRST #{displayCnt} ROWS ONLY

SELECT BOARD_NO , BOARD_TITLE , BOARD_CONTENT , BOARD_WRITER , TO_CHAR(REG_BOARD_DATE,'YYYY-MM-DD') AS REG_BOARD_DATE , TO_CHAR(REG_BOARD_DATE,'YYYY-MM-DD HH24:MI:SS') AS REG_BOARD_DATE_ORDER , READ_CNT , CATE_NO , REPLY_CNT , IS_PRIVATE , IS_NOTICE , DEPT_NO FROM UNI_BOARD WHERE DEPT_NO = #{deptNo}
AND TO_CHAR(REG_BOARD_DATE, 'YYYYMMDD' ) >= TO_CHAR(ADD_MONTHS(SYSDATE, #{month}), 'YYYYMMDD')
AND TO_CHAR(REG_BOARD_DATE, 'YYYY-MM-DD') >= #{fromDate} AND TO_CHAR(REG_BOARD_DATE, 'YYYY-MM-DD') <= #{toDate}
AND UPPER(${searchKeyword}) LIKE '%' || UPPER(#{searchValue}) || '%'
ORDER BY REG_BOARD_DATE DESC OFFSET #{offsetCnt} ROWS FETCH FIRST #{displayCnt} ROWS ONLY

SELECT COUNT(BOARD_NO)
FROM UNI_BOARD
WHERE BOARD_WRITER = #{boardWriter}

AND TO_CHAR(REG_BOARD_DATE, 'YYYYMMDD' ) >= TO_CHAR(ADD_MONTHS(SYSDATE, #{month}), 'YYYYMMDD') 


	AND TO_CHAR(REG_BOARD_DATE, 'YYYY-MM-DD') >= #{fromDate}
	AND TO_CHAR(REG_BOARD_DATE, 'YYYY-MM-DD') <= #{toDate}

mapper.insert:
		INSERT INTO UNI_BOARD(
          BOARD_NO
          , BOARD_TITLE
          , BOARD_CONTENT
          , BOARD_WRITER
          , REG_BOARD_DATE
          , READ_CNT
          , CATE_NO
          , REPLY_CNT
          , IS_PRIVATE
          , IS_NOTICE       
	    ) VALUES(
	         (SELECT 'BOARD_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(BOARD_NO, 7))), 0) +1 , 3, '0')
	            FROM DEPT_MANAGE)
	         , #{boardTitle}
	         , #{boardContent}
	         , #{stuNo}
	         , #{regBoardDate}
	         , #{readCnt}
	         , #{cateNo}
	         , #{replyCnt}
	         , #{isPrivate}
	         , #{isNotice}
	    )

		INSERT INTO UNI_BOARD(
          BOARD_NO
          , BOARD_TITLE
          , BOARD_CONTENT
          , BOARD_WRITER
          , REG_BOARD_DATE 
          , READ_CNT 
          , CATE_NO
          , REPLY_CNT
          , IS_PRIVATE 
          , IS_NOTICE
          , DEPT_NO 
	    ) VALUES(
	         (SELECT 'BOARD_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(BOARD_NO, 7))), 0) +1 , 3, '0')
	            FROM UNI_BOARD)
	         , #{boardTitle}
	         , #{boardContent}
	         , #{boardWriter}
	         , SYSDATE
	         , 0
	         , #{cateNo}
	         , 0
	         , #{isPrivate}
	         , #{isNotice}
	         , #{deptNo}
	    )

mapper.update:
UPDATE UNI_BOARD
SET READ_CNT = #{readCnt}
WHERE BOARD_NO = #{boardNo}

UPDATE UNI_BOARD
SET BOARD_CONTENT = #{boardContent}
	, BOARD_TITLE = #{boardTitle}
WHERE BOARD_NO = #{boardNo}
