mapper.insert:
INSERT INTO UNI_MEMBER (
		MEM_NO
		, MEM_PW
		, MEM_NAME
		, MEM_EMAIL
		, MEM_ADDR
		, MEM_ADDR_DETAIL

		, MEM_TELL

		, MEM_IMAGE
		, MEM_GENDER
		, MEM_BIRTH
		, MEM_ROLE
) VALUES(
		#{memNo}
		, #{memPw}
		, #{memName}
		, #{memEmail}
		, #{memAddr}
		, #{memAddrDetail}

		, #{memTell}

		, #{memImage}
		, #{memGender}
		, #{memBirth}
		, #{memRole}	
)

INSERT INTO MEM_IMG(
    IMG_CODE
    , ORIGIN_FILE_NAME
    , ATTACHED_FILE_NAME
    , IS_MAIN
    , MEM_NO
) VALUES(
	#{memImgVO.imgCode}
	, #{memImgVO.originFileName}
	, #{memImgVO.attachedFileName}
	, #{memImgVO.isMain}
	, #{memNo}
)

mapper.select:
SELECT MEM_NO
FROM UNI_MEMBER
WHERE MEM_NAME = #{memName}
AND MEM_EMAIL = #{memEmail}
