mapper.insert:
INSERT INTO LECTURE (
	LEC_NO
	, LEC_NAME
	, LEC_SCORE
	, COLL_NO
	, DEPT_NO
	, EMP_NO

	, MAX_MEM

	, SEM_NO
) VALUES (
	#{lecNo}
	, #{lecName}
	, #{lecScore}
	, #{collNo}
	, #{deptNo}
	, #{empNo}

	, #{maxMem}

	, #{semNo}
)

INSERT INTO LECTURE_PDF (
	PDF_NO
	, ORIGIN_PDF_NAME
	, ATTACHED_PDF_NAME
	, LEC_NO
) VALUES (
	(SELECT 'PDF_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(PDF_NO, 5))), 0) + 1, 4, '0')
	FROM LECTURE_PDF)
	, #{lecturePdfVO.originPdfName}
	, #{lecturePdfVO.attachedPdfName}
	, #{lecNo}
)

INSERT INTO LECTURE_TIME (
	TIME_NO
	, LEC_DAY
	, START_TIME
	, FINISH_DATE
	, LEC_NO
)

		SELECT (SELECT 'TIME_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(TIME_NO, 6))), 0) + 1 + #{i}, 4, '0')
		FROM LECTURE_TIME)
		, #{lectureTimeVO.lecDay}
		, #{lectureTimeVO.startTime}
		, #{lectureTimeVO.finishDate}
		, #{lecNo}
		FROM DUAL

mapper.select:
SELECT LEC.LEC_NO , LEC_NAME , LEC_SCORE , COLL_NAME , DEPT_NAME , MAX_MEM , NOW_MEM , LEC_DAY , START_TIME , FINISH_DATE , LEC_STATUS , SEM_NAME , UM.MEM_NAME FROM LECTURE LEC, COLLEAGE COL, DEPT DEP, UNI_MEMBER UM, LECTURE_TIME LT, SEMESTER SEM WHERE LEC.COLL_NO = COL.COLL_NO AND LEC.DEPT_NO = DEP.DEPT_NO AND UM.MEM_NO = LEC.EMP_NO AND LT.LEC_NO = LEC.LEC_NO AND SEM.SEM_NO = LEC.SEM_NO
AND EMP_NO = #{empNo}
AND COLL_NO = #{collNo}
AND DEPT_NO = #{deptNo}
AND LEC_NAME = #{lecName}
