mapper.insert:
INSERT INTO LECTURE (
	LEC_NO
	, LEC_NAME
	, LEC_SCORE
	, COLL_NO
	, DEPT_NO
	, EMP_NO

	, MAX_MEM

	, SEM_NO
) VALUES (
	#{lecNo}
	, #{lecName}
	, #{lecScore}
	, #{collNo}
	, #{deptNo}
	, #{empNo}

	, #{maxMem}

	, #{semNo}
)

mapper.select:
SELECT TIME_NO FROM LECTURE_TIME LT, LECTURE LEC WHERE LT.LEC_NO = LEC.LEC_NO AND LEC_DAY = #{lecDay} AND LEC_STATUS = 'Y'
AND LEC.LEC_NO != #{lecNo}
AND ( (START_TIME <= #{startTime} AND FINISH_TIME > #{startTime}) OR (START_TIME < #{finishTime} AND FINISH_TIME >= #{finishTime}) OR (START_TIME >= #{startTime} AND FINISH_TIME <= #{finishTime}) )

mapper.insert:
INSERT INTO LECTURE_PDF (
	PDF_NO
	, ORIGIN_PDF_NAME
	, ATTACHED_PDF_NAME
	, LEC_NO
) VALUES (
	(SELECT 'PDF_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(PDF_NO, 5))), 0) + 1, 4, '0')
	FROM LECTURE_PDF)
	, #{lecturePdfVO.originPdfName}
	, #{lecturePdfVO.attachedPdfName}
	, #{lecNo}
)

INSERT INTO LECTURE_TIME (
	TIME_NO
	, LEC_DAY
	, START_TIME
	, FINISH_TIME
	, LEC_NO
)

		SELECT (SELECT 'TIME_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(TIME_NO, 6))), 0) + 1 + #{i}, 4, '0')
		FROM LECTURE_TIME)
		, #{lectureTimeVO.lecDay}
		, #{lectureTimeVO.startTime}
		, #{lectureTimeVO.finishTime}
		, #{lecNo}
		FROM DUAL

mapper.select:
SELECT LEC.LEC_NO , LEC_NAME , LEC_SCORE , COLL_NAME , DEPT_NAME , MAX_MEM , NOW_MEM , LEC_DAY , START_TIME , FINISH_TIME , ATTACHED_PDF_NAME , ORIGIN_PDF_NAME , TO_CHAR(CREATE_DATE, 'YYYY') AS CREATE_DATE , LEC_STATUS , SEM_NAME , UM.MEM_NAME , TIME_NO , SEM.SEM_NO , LEC.DEPT_NO FROM LECTURE LEC , COLLEAGE COL , DEPT DEPT , UNI_MEMBER UM , LECTURE_TIME LT , SEMESTER SEM , LECTURE_PDF LP WHERE LEC.COLL_NO = COL.COLL_NO AND LEC.DEPT_NO = DEPT.DEPT_NO AND UM.MEM_NO = LEC.EMP_NO AND LT.LEC_NO = LEC.LEC_NO AND SEM.SEM_NO = LEC.SEM_NO AND LP.LEC_NO = LEC.LEC_NO
AND EMP_NO = #{empNo}
AND COLL_NO = #{collNo}
AND DEPT_NO = #{deptNo}
AND LEC_STATUS = #{lecStatus}
AND UPPER(LEC_NAME) LIKE '%'||UPPER(#{lecName})||'%'
AND LEC.LEC_NO = #{lecNo}
AND UPPER(${searchKeyword}) LIKE '%' || UPPER(#{searchValue}) || '%'
AND LEC.DEPT_NO = #{searchDept}
ORDER BY LEC_NO DESC

mapper.update:
UPDATE LECTURE
SET LEC_NAME = #{lecName}
, LEC_SCORE = #{lecScore}
, LEC_STATUS = #{lecStatus}
WHERE LEC_NO = #{lecNo}

UPDATE LECTURE_TIME
SET LEC_DAY = #{lecDay}
, START_TIME = #{startTime}
, FINISH_TIME = #{finishTime}
WHERE TIME_NO = #{timeNo}

UPDATE STU_GRADE
SET GRADE = #{grade}
WHERE STU_GRADE_NO = #{stuGradeNo}
