mapper.insert:
	INSERT INTO BOARD_REPLY (
		REPLY_NO
		, BOARD_NO
		, REPLY_CONTENT
		, REPLY_WRITER
		, IS_PRIVATE
	) VALUES (
		(SELECT 'REPLY_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(REPLY_NO, 7))), 0) +1 , 3, '0')
            FROM BOARD_REPLY)
        , #{boardNo}
        , #{replyContent}
        , #{replyWriter}
        , 'N'
	)
