mapper.insert:
INSERT INTO DEPT_MANAGE(
APPLY_NO
, STU_NO
, APPLY_CODE
, APPLY_DATE
, APPLY_REASON
, APPLROVAL_DATE
, PROCESS_STATUS
, FROM_COLL
, TO_COLL
, FROM_DEPT
, TO_DEPT
, DOUBLE_MAJOR_COLL
, DOUBLE_MAJOR_DEPT
, STU_YEAR
, STU_SEM
) VALUES(
(SELECT 'APPLY_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(APPLY_NO, 7))), 0) +1 , 3,
'0')
FROM DEPT_MANAGE)
, #{stuNo}
, #{applyCode}
, SYSDATE
, #{applyReason}
, TO_DATE('9999-12-30', 'YYYY-MM-DD')
,'승인대기'
, #{fromColl}
, #{toColl}
, #{fromDept}
, #{toDept}
, #{doubleMajorColl}
, #{doubleMajorDept}
, #{stuYear}
, #{stuSem}
)

mapper.update:
UPDATE UNI_STU
SET PARENT_NAME = #{parentName}
, PARENT_TELL = #{parentTell}
, STU_BANK_NAME = #{stuBankName}
, STU_BANK_ACCOUNT = #{stuBankAccount}
WHERE MEM_NO = #{memNo}

UPDATE UNI_MEMBER
SET MEM_ADDR = #{memAddr}
, MEM_ADDR_DETAIL = #{memAddrDetail}
WHERE MEM_NO = #{memNo}

UPDATE MEM_IMG
SET ORIGIN_FILE_NAME =
#{originFileName}
, ATTACHED_FILE_NAME = #{attachedFileName}
WHERE MEM_NO = #{memNo}

UPDATE UNI_MEMBER
SET MEM_PW = #{memPw}
WHERE MEM_NO =
#{memNo}

mapper.select:
SELECT COUNT(ENROLLMENT_NO)
FROM ENROLLMENT
WHERE STU_NO = #{stuNo}
AND LEC_NO = #{lecNo}

mapper.insert:
INSERT INTO ENROLLMENT (
ENROLLMENT_NO
, STU_NO
, LEC_NO
, SEM_NO
) VALUES (
(SELECT 'ENROL_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(ENROLLMENT_NO, 7))), 0) +1,
LENGTH(NVL(MAX(TO_NUMBER(SUBSTR(ENROLLMENT_NO, 7))), 0) +1) , '0')
FROM ENROLLMENT)
, #{stuNo}
, #{lecNo}
, #{semNo}
)

mapper.update:
UPDATE LECTURE
SET NOW_MEM = #{nowMem} + 1
WHERE LEC_NO = #{lecNo}

mapper.insert:
INSERT INTO STU_GRADE (
	STU_GRADE_NO
	, LEC_NO
	, GRADE
	, STU_NO
	, SEM_NO
) VALUES(
(SELECT 'STU_GRADE_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(STU_GRADE_NO, 11))), 0) +1,
		LENGTH(NVL(MAX(TO_NUMBER(SUBSTR(STU_GRADE_NO, 11))), 0) +1) , '0') FROM STU_GRADE)
, #{lecNo}
, NULL
, #{stuNo}
, #{semNo}
)

mapper.delete:
DELETE ENROLLMENT
WHERE STU_NO = #{stuNo}
AND LEC_NO = #{lecNo}
AND SEM_NO = #{semNo}

DELETE STU_GRADE
WHERE STU_NO = #{stuNo}
AND LEC_NO = #{lecNo}
AND SEM_NO = #{semNo}

mapper.update:
UPDATE LECTURE
SET NOW_MEM = #{nowMem} - 1
WHERE LEC_NO = #{lecNo}
